test('StrokeJoin.Miter', (t) => {
  const box = new Path2D()
  box.rect(0, 0, 100, 100)
  box.stroke({ width: 20, join: StrokeJoin.Miter })

  t.is(box.toSVGString(), 'M-10 -10L110 -10L110 110L-10 110L-10 -10ZM10 10L10 90L90 90L90 10L10 10Z')
})

